func getStatusClass(status string) string {
	switch status {
	case "Connected":
		return "alert-success"
	case "Disconnected":
		return "alert-secondary"
	case "Connecting...":
		return "alert-info"
	default:
		if len(status) >= 5 && status[:5] == "Error" {
			return "alert-danger"
		}
		return "alert-warning"
	}
}

